    dateCreate *=1000;
    var diff = new Date() - dateCreate;
    if (diff < 1000) {
        return 'щойно';
    }
    var sec = Math.floor(diff / 1000);
    if (sec < 60) {
        return sec + ' сек. тому';
    }
    var min = Math.floor(diff / (60000));
